import React from 'react';
import { Link } from 'react-router-dom';

function ProductCard(props) {
  let date = new Date(1000 * props.product.last_updated_at);
  if (props.product.usd_24h_change) {
    date = (
      <div>{`24h Change: %${props.product.usd_24h_change.toFixed(2)}`}</div>
    );
  }
  return (
    <div className={'border-2 p-4 mb-5'}>
      <div>{props.name}</div>
      <div>{`USD: $${props.product.usd}`}</div>
      <div>{`Updated At: ${date.toLocaleString()}`}</div>
      {date}
    </div>
  );
}

export default ProductCard;
