import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Loader from '../Components/Loader';

function Home() {
  const url = `https://5e9623dc5b19f10016b5e31f.mockapi.io/api/v1/products?page=1&limit=10`;
  const [products, setProducts] = useState({
    loading: false,
    data: null,
    error: false,
  });

  //   This helps so that the browser doesn't send infinite requests, it only changes on url changing

  useEffect(() => {
    setProducts({
      loading: true,
      data: null,
      error: false,
    });
    axios
      .get(url)
      .then((response) => {
        setProducts({
          loading: false,
          data: response.data,
          error: false,
        });
      })
      .catch(() => {
        setProducts({
          loading: false,
          data: null,
          error: true,
        });
      });
  }, [url]);

  let content = null;

  if (products.error) {
    content = (
      <div>
        <div className="bg-red-300 p-3">
          There was an error please refresh or try again later.
        </div>
      </div>
    );
  }

  if (products.loading) {
    content = <Loader></Loader>;
  }

  if (products.data) {
    content = (
      <div>
        <h1 className="text-2xl font-bold mb-3">{product.data.name}</h1>
        <div className="flex">
          <img src={product.data.sprites.back_default} alt={product.name} />
          <img src={product.data.sprites.front_default} alt={product.name} />
        </div>
        <div className="text-xl mb-3">
          Main Abilities:{' '}
          <span className="font-bold text-xl mb-3">
            {product.data.abilities[0].ability.name}
          </span>
        </div>
        <div>
          Lorem Ipsum Detal Mega Manga Chone Ipsum Lorem Ipsum Detal Mega Manga
          Chone Ipsum Lorem Ipsum Detal Mega Manga Chone Ipsum Lorem Ipsum Detal
          Mega Manga Chone Ipsum{' '}
        </div>
      </div>
    );
  }

  return (
    <div>
      <h1 className="font-bold text-2xl mb-3">Homebrew Pokedex</h1>
      {content}
    </div>
  );
}

export default Home;
