import React from 'react';
import Loader from '../Components/Loader';
import ProductCard from '../Components/ProductCard';
import { useAxiosGet } from '../Hooks/httpRequests';

function Home() {
  const url = `https://api.coingecko.com/api/v3/simple/price?ids=bitcoin%2Cethereum&vs_currencies=usd&include_market_cap=true&include_24hr_vol=true&include_24hr_change=true&include_last_updated_at=true`;

  let products = useAxiosGet(url);

  let content = null;

  if (products.error) {
    content = (
      <div>
        <div className="bg-red-300 p-3">
          There was an error please refresh or try again later.
        </div>
      </div>
    );
  }

  if (products.loading) {
    content = <Loader></Loader>;
  }

  if (products.data) {
    // content = products.data.map((product, key) => (
    // <div key={product.id}>
    //   <ProductCard product={product} />
    // </div>
    // ));
    <div>{console.log(products.data.bitcoin)}</div>;
  }

  return (
    <div>
      <h1 className="font-bold text-2xl mb-3">Homebrew Pokedex</h1>
      {content}
    </div>
  );
}

export default Home;
