import React from 'react';

function Home() {
  const { id } = useParams();
  const url = `https://pokeapi.co/api/v2/pokemon/${id}`;
  const [product, setProduct] = useState({
    loading: false,
    data: null,
    error: false,
  });
  let content = null;

  //   This helps so that the browser doesn't send infinite requests, it only changes on url changing

  useEffect(() => {
    setProduct({
      loading: true,
      data: null,
      error: false,
    });
    axios
      .get(url)
      .then((response) => {
        setProduct({
          loading: false,
          data: response.data,
          error: false,
        });
      })
      .catch(() => {
        setProduct({
          loading: false,
          data: null,
          error: true,
        });
      });
  }, [url]);

  return (
    <div>
      <h1 className="font-bold text-2xl mb-3">Homebrew Pokedex</h1>
      {content}
    </div>
  );
}

export default Home;
